import React from "react";
import gsap from "gsap";
import { useGSAP } from "@gsap/react";
const Section = () => {
  useGSAP(() => {
    gsap.from("section > nav > *", {
      opacity: 0,

      scale: 0.1,
      duration: 1,
      stagger: 0.2,

      ease: "power4.out",
    });
    gsap.from("main > span", {
      opacity: 0,
      x: 30,
      duration: 0.5,
      stagger: 0.5,
      ease: "power4.out",
    });
    gsap.from("#section > div", {
      opacity: 0,
      x: 30,
      duration: 0.5,
      stagger: 0.2,
      ease: "power4.out",
    });
  });
  return (
    <section
      className="min-w-[30%] md:min-w-[25%]  z-2   h-full bg-[#222222] text-white rounded-xl"
      style={{ padding: "0.5rem 0" }}
    >
      <nav
        className="flex gap-2 justify-end items-center"
        style={{ marginBottom: "2rem" }}
      >
        <h5 className="text-[0.8rem] font-bold">T. Weeknd</h5>
        <img
          className="w-[3rem] h-[3rem] rounded-full "
          src="data:image/jpeg;base64,"
          alt="Profile Pic"
        />
      </nav>
      <main className="text-[1rem] flex  flex-col ">
        <span
          className="flex justify-between items-center "
          style={{ padding: "0 0.5rem", marginBottom: "1rem" }}
        >
          <h4>Now playing</h4>
          <p className="text-[0.8rem]">View all</p>
        </span>
        {/* Tracks Queue Cards */}
        <div id="section" className="flex flex-col gap-4 overflow-y-scroll">
          <div
            className=" hover:bg-[#4e4e4e] min-w-[40%] h-[3rem] gap-2 flex justify-between items-center rounded-lg"
            style={{ padding: "0 0.5rem" }}
          >
            <img
              className=" object-cover h-full w-[20%] rounded-md"
              src="https://i.pinimg.com/736x/16/41/d4/1641d44d98048fbb0befea634248ee54.jpg"
              alt="Song Cover"
            />
            <h4 className="text-[0.9rem] font-bold self-start  w-[55%] h-full">
              StarBoy
              <span className="block  font-light text-[0.7rem]">
                - The Weeknd
              </span>{" "}
            </h4>
            <p className="text-[0.6rem]">3:13</p>
          </div>
          <div
            className="hover:bg-[#4e4e4e] min-w-[40%] h-[3rem] gap-2 flex justify-between items-center rounded-lg"
            style={{ padding: "0 0.5rem" }}
          >
            <img
              className=" object-cover h-full w-[20%] rounded-md"
              src="data:image/jpeg;base64,"
              alt="Song Cover"
            />
            <h4 className="text-[0.9rem] font-bold self-start  w-[55%] h-full">
              StarBoy
              <span className="block  font-light text-[0.7rem]">
                - The Weeknd
              </span>{" "}
            </h4>
            <p className="text-[0.6rem]">3:13</p>
          </div>
          <div
            className="hover:bg-[#4e4e4e] min-w-[40%] h-[3rem] gap-2 flex justify-between items-center rounded-lg"
            style={{ padding: "0 0.5rem" }}
          >
            <img
              className=" object-cover h-full w-[20%] rounded-md"
              src="data:image/jpeg;base64,"
              alt="Song Cover"
            />
            <h4 className="text-[0.9rem] font-bold self-start  w-[55%] h-full">
              StarBoy
              <span className="block  font-light text-[0.7rem]">
                - The Weeknd
              </span>{" "}
            </h4>
            <p className="text-[0.6rem]">3:13</p>
          </div>
          <div
            className="hover:bg-[#4e4e4e] min-w-[40%] h-[3rem] gap-2 flex justify-between items-center rounded-lg"
            style={{ padding: "0 0.5rem" }}
          >
            <img
              className=" object-cover h-full w-[20%] rounded-md"
              src="data:image/jpeg;base64,"
              alt="Song Cover"
            />
            <h4 className="text-[0.9rem] font-bold self-start  w-[55%] h-full">
              StarBoy
              <span className="block  font-light text-[0.7rem]">
                - The Weeknd
              </span>{" "}
            </h4>
            <p className="text-[0.6rem]">3:13</p>
          </div>
        </div>
      </main>
    </section>
  );
};

export default Section;
